import React, { useEffect, useState } from "react";
import productList from "./products";
import Products from "./pages/Products";
import ProductDetails from "./pages/ProductDetails";
function App() {
  const [values, setValues] = useState({
    page: "products",
    products: [],
    product: null,
  });

  const onChange = (product) =>
    setValues((prev) => {
      return {
        ...prev,
        product: product,
      };
    });

  useEffect(
    () =>
      setValues((prev) => {
        return {
          ...prev,
          products: productList,
        };
      }),
    []
  );

  return (
    <div className="app">
      {values.page === "products" && values.products.length > 1 && (
        <Products productList={values.products} onChange={onChange} />
      )}
      {values.product && values.page == "product-details" && (
        <ProductDetails product={values.product} />
      )}
    </div>
  );
}

export default App;
